import React, { useState } from 'react';
import Switch from 'react-switch';
import '../userInformation.css'
export default function ResearchForm() {
    const [checked, setChecked] = useState(false);

    const handleChange = (nextChecked) => {
        setChecked(nextChecked);
    };
    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(e.target);
    }
  return (
    <div className="information-forms-container">
        <h1></h1>
      <form onSubmit={handleSubmit} className = "form-content">
        <div className='form-group'>
            <label className ="form-label"> Are you involved in any research at present?</label>
            <div>
                <Switch
                    onChange={handleChange}
                    checked={checked}
                    offColor="#888"
                    onColor="#1f1"
                />
                <p>{checked ? "Yes" : "No"}</p>
            </div>
          </div>

          <div className='form-group'>
            <label className ="form-label"> If yes please list out all of them and give a brief description about the research and what were your major roles.{"(Please be descriptive since this information will be used to search similar research for you.) "}</label>
            <div>

            </div>
          </div>



       <div className='button-group'>
          <button className = "button-design" >
            <span>
            <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18" fill="currentColor" class="bi bi-arrow-left" viewBox="0 0 16 16">
              <path fill-rule="evenodd" d="M15 8a.5.5 0 0 0-.5-.5H2.707l3.147-3.146a.5.5 0 1 0-.708-.708l-4 4a.5.5 0 0 0 0 .708l4 4a.5.5 0 0 0 .708-.708L2.707 8.5H14.5A.5.5 0 0 0 15 8"/>
           </svg>
           </span>
            <span>Previous page</span>
          </button>
            <button type="submit" className = "button-design"><span>Next page</span>
          <span>
            <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18" fill="currentColor" class="bi bi-arrow-right" viewBox="0 0 16 16">
              <path fill-rule="evenodd" d="M1 8a.5.5 0 0 1 .5-.5h11.793l-3.147-3.146a.5.5 0 0 1 .708-.708l4 4a.5.5 0 0 1 0 .708l-4 4a.5.5 0 0 1-.708-.708L13.293 8.5H1.5A.5.5 0 0 1 1 8"/>
           </svg>
           </span>
          </button>
        </div>
    </form>
    </div>
  )
}
