import React, { useState } from 'react';
import '../userInformation.css';

export default function UserInformation() {
  let pageNum = 0;
  const pageInformation = [
  { id: 1, title: 'Personal Information', assets: [
    {fieldName: "First Name", type:'text'}, {fieldName: "Last Name", type:'text'}, {fieldName: "Date of Birth",  type:'date'}, {fieldName: "Address",type:'text'}
] },
  { id: 2, title: 'Login Information', assets: [
    {fieldName: "School Email", type:'email'}, {fieldName: "Password",  type:'password'}, {fieldName: "Confirm Password",  type:'password'}, {fieldName: "Username", type:'text'}, {fieldName: "Student ID",  type:'number'}
] },
{ id: 3, title: 'Education', assets: [
  {fieldName: "Major", type:'text'}, {fieldName: "Year in College", type:'text'}, {fieldName: "Graduation Date",type:'date'}, {fieldName: "Department",  type:'text'}
] }

  ]

  const [pageData, setPageData] = useState(pageInformation[pageNum]

  );
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    dateOfBirth: '',
    address: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data:', formData);
    // You can add form submission logic here, like sending the data to an API
  };

  return (
    <div className="information-forms-container">
      <h3>Personal Information</h3>
      <form onSubmit={handleSubmit} className='form-content'>
        {createForm(pageInformation[0].assets[0].type, "firstName", formData, pageInformation[0].assets[0].fieldName, handleChange)}
        {createForm(pageInformation[0].assets[1].type, "lastName", formData,  pageInformation[0].assets[1].fieldName, handleChange)}
        {createForm(pageInformation[0].assets[2].type, "dateOfBirth", formData, pageInformation[0].assets[2].fieldName, handleChange)}
        {createForm(pageInformation[0].assets[3].type, "address", formData, pageInformation[0].assets[3].fieldName,handleChange)}
        <div className='button-group'>
          <button type="submit" className = "button-design">
            <span>
            <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18" fill="currentColor" class="bi bi-arrow-left" viewBox="0 0 16 16">
              <path fill-rule="evenodd" d="M15 8a.5.5 0 0 0-.5-.5H2.707l3.147-3.146a.5.5 0 1 0-.708-.708l-4 4a.5.5 0 0 0 0 .708l4 4a.5.5 0 0 0 .708-.708L2.707 8.5H14.5A.5.5 0 0 0 15 8"/>
           </svg>
           </span>
            <span>Previous page</span>
          </button>
          <button type="submit" className = "button-design"><span>Next page</span>
          <span>
            <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18" fill="currentColor" class="bi bi-arrow-right" viewBox="0 0 16 16">
              <path fill-rule="evenodd" d="M1 8a.5.5 0 0 1 .5-.5h11.793l-3.147-3.146a.5.5 0 0 1 .708-.708l4 4a.5.5 0 0 1 0 .708l-4 4a.5.5 0 0 1-.708-.708L13.293 8.5H1.5A.5.5 0 0 1 1 8"/>
           </svg>
           </span>
          </button>
        </div>
      </form>
    </div>
  );
}
const createForm = (type, name, formData, fieldName, handlingFunction) =>{
  return <div className="form-group">
          <label htmlFor={name} className="form-label">{fieldName}</label>
          <input 
            type= {type} 
            id={name}
            name={name}
            value={formData[name]} 
            onChange={handlingFunction} 
            className="form-input"
          />
      </div>
}
